  function handleUpload(imageString, name){
    Service.send({
      method: 'post', path: 'Upload/uploadMediaFile', data: {
        imageData: imageString,
        imageFormat: "png"
      },
    }).then(result => {
      if (result) {
        const { statusCode, data: imageData } = result
        if (statusCode === 200) {
          setData({
            ...data,
            [name]: imageData
          })
          
        }
      } 
    })
  }


  function handleFetchList() {
    Service.send({
