        filter: {
         
        },
        skip: 0,
        limit: 20,
        order: {
          key: "createdAt",
          value: "desc"
        }
      }, query: null,
    }).then(res => {
      if (res) {
        const { statusCode, data, message } = res

        if (statusCode === 200) {
          
          setItems(data.data)
        } 
      } 
    })
  }

